private void cancel_button_Click(object sender, System.Web.UI.ImageClickEventArgs e)
{
    if(this.Session["ReturnURL"] != null)
    {
       this.Response.Redirect( this.Session["ReturnURL"].ToString() );
    }
    else
    {
       this.Response.Redirect("home.aspx");
    }
}

